function generateFilterUrl(new_values) {
  values = {
    order: 'lastupdate',
    anime: '0',
    sortid: '0',
    typeid: '0',
    words: '0',
    rgroupid: '0',
    update: '0',
    isfull: '0',
    page: '{{page}}',
  }
  for (key in new_values) {
  	  values[key]=new_values[key]
  	}
  return source.bookSourceUrl + '/wenku/' + values.order + '_' + values.anime + '_' + values.sortid + '_' + values.typeid + '_' + values.words + '_' + values.rgroupid + '_' + values.update + '_' + values.isfull + '_' + values.page + '.html';
}

//书架
user = ajax(source.bookSourceUrl + "/user.php")
//java.toast(user)
try {
if (user.match(/<h2 class="aui-center-title">(登录|错误).*轻之文库轻小说.+<\/h2>/gi)) {
  //未登录
  res.push(
    {
      title: `>> 我的书架 | 未登录 <<`,
      url: '',
      style: { layout_flexGrow: 1, layout_flexBasisPercent: 1 }
    })
} else {
  //已登录
  res.push(
    {
      title: `>> 我的书架 | ${user.match(/<span class="user-name">.+<\/span>/gi)[0].replace(/<\/?span.*?>/gi,'')} <<`,
      url: '',
      style: { layout_flexGrow: 1, layout_flexBasisPercent: 1 }
    })
  bookcase = ajax(source.bookSourceUrl + "/bookcase.php")
  bookcase.match(/<select[\s\S]+?<\/select>/gi)[0].match(/<option.+<\/option>/gi).forEach(option => {
    res.push(
      {
        title: option.match(/>.*(?=<)/gi)[0].replace('>', ''),
        url: source.bookSourceUrl + "/bookcase.php?classid=" + option.match(/value=\".+?(?=\")/gi)[0].replace(/(value=\")/gi, ''),
        style: { layout_flexGrow: 1 }
      })
  })
}
} catch (e) {
  throw("书架加载失败：\n"+e+"\n登录页源码：\n"+user)
}

content = ajax(source.bookSourceUrl + "/wenku/")
content.match(/排序[\s\S]+?<\/div>/gi)[0].match(/<a.+<\/a>/gi).forEach(a => {
  let filter = {}
  let sortTitle = a.match(/>.+(?=<)/gi)[0].replace('>', '')
  let type = "order"
  let value = a.match(/\/[^_/]+(?=_)/gi)[0].replace('/', '')
  filter[type]=value;
  content.match(/作品分类[\s\S]+?<\/div>/gi)[0].match(/<a.+<\/a>/gi).forEach((v, i) => {
    let v_type = "anime"
    let v_value = v.match(/\/[^_/]+_\d+(?=_)/gi)[0].replace(/\/[^_/]+_/gi, '')
    filter[v_type]=v_value;
    if (i == 0) {
      res.push(
        {
          title: `>> 作品分类 | ${sortTitle} <<`,
          url: generateFilterUrl(filter),
          style: { layout_flexGrow: 1, layout_flexBasisPercent: 1 }
        })
    } else {
      res.push(
        {
          title: v.match(/>.+(?=<)/gi)[0].replace('>', ''),
          url: generateFilterUrl(filter),
          style: { layout_flexGrow: 1 }
        })
    }
  })
})

content.match(/排序[\s\S]+?重置/gi)[0].match(/<li[\s\S]+?<\/li>/gi).forEach(li => {
  let sortTitle = li.match(/>.+(?=<\/h3>)/gi)[0].replace('>', '')
  res.push(
    {
      title: `>> ${sortTitle} <<`,
      url: '',
      style: { layout_flexGrow: 1, layout_flexBasisPercent: 1 }
    })
  li.match(/<a.+<\/a>/gi).forEach((v, i) => {
    if (i != 0) {
      res.push(
        {
          title: v.match(/>.+(?=<)/gi)[0].replace('>', ''),
          url: v.match(/href="[^"]+(?=")/gi)[0].replace('href="',"").replace("1.html","{{page}}.html"),
          style: { layout_flexGrow: 1 }
        })
    }
  })
})
tagContent = ajax(source.bookSourceUrl + "/taglist/1.html")
res.push(
  {
    title: '>> 标签分类 <<',
    url: '',
    style: { layout_flexGrow: 1, layout_flexBasisPercent: 1 }
  })
try {
tagContent.match(/标签分类<\/h3>[\s\S]+?重置/gi)[0].match(/<a.+<\/a>/gi).forEach((v, i) => {
  res.push(
    {
      title: v.match(/>.+(?=<)/gi)[0].replace('>', '').replace("&nbsp",""),
      url: v.match(/href="[^"]+(?=")/gi)[0].replace('href="',"").replace("1.html","{{page}}.html"),
      style: { layout_flexGrow: 1 }
    })
})
} catch (e) {
  throw("标签页解析错误："+e+"\n标签页源码：\n"+tagContent)
}
JSON.stringify(res)
//throw(JSON.stringify(res))

jsLib:
function errorReg() {
  return /(java.net.+Exception)|(okhttp.+Exception)|(error code: 1015)|(503 Service Temporarily Unavailable)|(Parse error.+in.+wwwroot.+php)|(_cf_chl_opt|challenges.css)|(<center.+>.+不支持)|(章节内容不支持该浏览器显示)/gi
}
function ajax(url) {
  const { java, cookie } = this;
  let content = "";
  let time=1;
  	content = java.ajax(url);
  	while (String(content).match(errorReg())) {
    if (time>10) {
  	    java.toast("重试10次后仍请求失败："+url)
  	    throw("重试10次后仍请求失败："+url+content)
  	    break;
    	}
    	if(!!String(content).match(/不支持/gi)) {
      //java.log(cookie.getCookie(url));
      cookie.replaceCookie(url,java.webView(null,url,"document.cookie"));
    	}
    	if (!!String(content).match(/_cf_chl_opt|challenges.css/gi)) {
    		 i = 1;
      	while (!!String(content).match(/_cf_chl_opt|challenges.css/gi)) {
	       java.log('盾');
	       java.log(content);
        if (i <= 1) {
          java.toast("哦呼，五秒盾，正在尝试静默破盾ing……");
          content = java.webView(null,url,null);
        } else {
          java.toast("啊哦，静默破盾好像不管用，试试手动过校验吧！");
          content = java.startBrowserAwait(url,"加载完毕后点完成，此页面可能会弹出多次").body();
        }
        i++;
      }
      continue;
    }
  		 java.log("请求失败："+url)
  		 java.log("重试"+time+"次")
  		 let t=new Date().getTime()
    while (new Date().getTime() - t < 500) { }
    if (String(content).match(/1015|503|php/gi)) {
    	  while (new Date().getTime() - t < 5000) { }
    	}
  		 content = java.ajax(url);
  		 time++;
  	}
  	return String(content);
}

loginUrl:
@js:
function login() {
  let une = source.getLoginInfoMap().get("账号")
  let pwd = source.getLoginInfoMap().get("密码")
  if (une && pwd) {
    let body = String('username=' + une + '&password=' + pwd + '&usecookie=86400&act=login')
    let url = source.bookSourceUrl + '/login.php?do=submit'
    let res = java.post(url, body, {
      "Content-Type": "application/x-www-form-urlencoded"
    })
    let ck = res.cookies()
    if (res.body().match(/错误/gi)) {
      throw(res.body().match(/<div class="aui-ver-form">[\s\S]+<br>/gi)[0].replace(/<.+>\s*/gi,""))
    }
    let header = JSON.stringify({
      "Cookie": String(ck).match(/\{(.*?)\}/)[1].replace(/,/g, ';')
    })
    source.putLoginHeader(header)
  }
}

