jsLib:
function getConfig() {
  const { book, source, java } = this
  const config = {
  	  language: "中日",
  	  translation: "SGYB",
  	  translationArr: []
  	}
  	const languages = [
  	  "日文",
    "中文",
    "中日",
    "日中"
  ]
  const translations = {
  	  B: "baiduParagraphs",
  	  Y: "youdaoParagraphs",
  	  G: "gptParagraphs",
  	  S: "sakuraParagraphs"
  	}
  	function applyTran(tranStr) {
  	  config.translation=tranStr
  	  if (tranStr==="并列") tranStr="SGYB"
  	  let tranArr = []
  	  for (let i=0; i<tranStr.length; i++) {
  	  	  tranArr.push(translations[tranStr.charAt(i)])
  	  	}
  	  	config.translationArr=tranArr
  	}
  	function applyLanguage(lang) {
  	  if (languages.includes(lang))
  	  	  config.language=lang
  	}
  applyTran(config.translation)
  const sVar = String(source.getVariable()).replace(/\s/gi, '').split('-')
  if (sVar[0]) applyLanguage(sVar[0])
  if (sVar[1]) applyTran(sVar[1])
  const bVar = String(book.getVariable("custom")).replace(/\s/gi, '').split('-')
  if (bVar[0]) applyLanguage(bVar[0])
  if (bVar[1]) applyTran(bVar[1])
  return config
}

