                 next = String(java.ajax("https://www.bilinovel.com" + nextLink));
                if(next != "error code: 1015"){break;}
                AimTime = Date.now()+3000;
                while(AimTime > Date.now()){}
            }
            str = next.match(/<script type\=\"text\/javascript\">var ReadParams.*/)
            path = String(str).match(/url_previous\:'.*?html/)[0].replace("url_previous:'", "")
            java.log(path)

            res[i] = String(res[i]).replace(/javascript:cid\(.*\)/, path)
        }
    }
}
